-- ----------------------------
-- View structure for word_sentence_count
-- ----------------------------
DROP VIEW IF EXISTS `word_sentence_count`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `word_sentence_count` AS select `word`.`word_id` AS `word_id`,`word`.`word` AS `word`,count(`sentence`.`sentence`) AS `sentencecount` from ((`word` join `instance`) join `sentence`) where ((`word`.`word_id` = `instance`.`word_id`) and (`instance`.`sentence_id` = `sentence`.`sentence_id`)) group by `word`.`word`;

-- ----------------------------
-- View structure for word_translation_count
