-- ----------------------------
-- Procedure structure for word_sentence
-- ----------------------------
DROP PROCEDURE IF EXISTS `word_sentence`;
delimiter ;;
CREATE PROCEDURE `word_sentence`(in searchword varchar(40))
  DETERMINISTIC
BEGIN
	SELECT `word`, `sentence` 
	from `instance` ,word, `sentence` 
	WHERE `instance`.`word_id` = `word`.`word_id` and `instance`.`sentence_id` = `sentence`.`sentence_id` and `word`.`word` = searchword;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for word_translation
-- ----------------------------
DROP PROCEDURE IF EXISTS `word_translation`;
delimiter ;;
CREATE PROCEDURE `word_translation`(in searchWord varchar(40))
  DETERMINISTIC
BEGIN
	SELECT translation as 翻译
	FROM `word`, `translate`,  `translation`
	WHERE `word`.`word_id` = `translate`.`word_id` and `translation`.`trans_id` = `translate`.`trans_id` and `word`.`word` = searchWord;
END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table translation
