DROP TRIGGER IF EXISTS `translation_update`;
delimiter ;;
CREATE TRIGGER `translation_update` BEFORE UPDATE ON `translation` FOR EACH ROW begin
    if not exists (select translation from translation where translation.translation = new.translation) then
        set new.translation = old.translation;
    end if;
end
;;
delimiter ;

