-- Triggers structure for table word
-- ----------------------------
DROP TRIGGER IF EXISTS `word_update`;
delimiter ;;
CREATE TRIGGER `word_update` BEFORE UPDATE ON `word` FOR EACH ROW begin
    if not exists (select word from word where word.word = new.word) then
        set new.word = old.word;
    end if;
end
;;
delimiter ;

SET FOREIGN_KEY_CHECKS = 1;
